import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import "./adduser.css";
import axios from "axios";
import { toast, Toast } from "react-toastify";

const initialState = {
  FirstName: "",
  LastName: "",
  Location: "",
  Email: "",
  DOB: "",
  Education: "",
};

const Adduser = () => {
  const [state, Setstate] = useState(initialState);
  const { FirstName, LastName, Location, Email, DOB, Education } = state;
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!FirstName || !LastName || !Location || !Email || !DOB || !Education) {
      toast.error("please provide value into each input field");
    } else {
      axios
        .post("http://localhost:5000/api/post", {
          FirstName,
          LastName,
          Location,
          Email,
          DOB,
          Education,
        })
        .then(() => {
          Setstate({
            FirstName: "",
            LastName: "",
            Location: "",
            Email: "",
            DOB: "",
            Education: "",
          });
        })
        .catch((err) => toast.error(err.res.data));
      setTimeout(() => navigate("/"), 500);
    }
  };
  const handleInputChange = (e) => {
    const { FirstName, LastName, Location, Email, DOB, Education, value } =
      e.target;
    Setstate({
      ...state,
      [FirstName]: value,
      [LastName]: value,
      [Location]: value,
      [Email]: value,
      [DOB]: value,
      [Education]: value,
    });
  };
  return (
   
     <div className="container">
    <form>
     <div>
      <label></label>
     </div>

    </form>


     </div>


  





  );
};

export default Adduser;




















