import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./home.css";
import axios from "axios";
import { FaUserEdit } from "react-icons/fa";
import { RiDeleteBinLine } from "react-icons/ri";

function Home() {
  const [data, setData] = useState([]);
  const loadData = async () => {
    const res = await axios.get("http://localhost:5000/api/get");
    setData(res.data);
  };
  useEffect(() => {
    loadData();
  }, []);
  return (
    <div style={{ marginTop: "180px" }}>
 
    <div className="head" style={{marginTop:"100px"}}>
 <h1> Student Management system</h1>
    </div>


      <table className="styled-table">
        <thead>
          <tr>
            <th style={{ textAlign: "center" }}>ID</th>
            <th style={{ textAlign: "center" }}>FirstName</th>
            <th style={{ textAlign: "center" }}>LastName</th>
            <th style={{ textAlign: "center" }}>Location</th>
            <th style={{ textAlign: "center" }}>Email</th>
            <th style={{ textAlign: "center" }}>DOB</th>
            <th style={{ textAlign: "center" }}>Education</th>
            <th style={{ textAlign: "center" }}>Action</th>
            <th style={{ textAlign: "center" }}>Delete</th>
          </tr> 
        </thead>
        <tbody>
          {data.map((item, index) => {
            return (
              <tr key={item.id}>
                <th scope="row">{index + 1}</th>
                <td>{item.FirstName}</td>
                <td>{item.LastName}</td>
                <td>{item.Location}</td>
                <td>{item.Email}</td>
                <td>{item.DOB}</td>
                <td>{item.Education}</td>
                <td>
                  <Link to={`/update/${item.id}`}>
                    <button className="btn btn-edit">
                      <FaUserEdit />
                      Edit
                    </button>
                  </Link>
                </td>
                <td>
                  <Link>
                    <button className="btn btn-delete">
                      <RiDeleteBinLine />
                      Delete
                    </button>
                  </Link>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
