import React, { useState, useEffect } from "react";
import { useParams, useHistory, Link } from "react-router-dom";
import "./adduser.css";
import axios from "axios";
import { toast, Toast } from "react-toastify";

const initialState = {
  name: "",
  date: "",
  email: "",
};

const Adduser = () => {
  const [state, Setstate] = useState(initialState);
  const { name, date, email } = state;
  const handleSubmit = (e) => {
    e.preventDefault();
    if(!name || !email || !date){
        toast.error("please provide value into each input field")
    }
    else{
        axios.post("http://localhost:5000/api/post",{
            FirstName, LastName, Location, Email, DOB, Education
        }).then(()=>{
            Setstate({FirstName:"", LastName:"", Location:"", Email:"", DOB:"", Education:""})
        }).catch((err)=>toast.error(err.res.data));
        setTimeout(()=>{

        },timeout)
    }
  };
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    Setstate({ ...state, [name]: value });
  };
  return (
    <div style={{ marginTop: "100px" }} className="container">
      <header>Add student details</header>
      <form
        style={{
          margin: "10px",
          padding: "15px",
          maxWidth: "900px",
          alignContent: "center",
        }}
        onSubmit={handleSubmit}
      >
        <div className="field">
          <div className="col-25">
            <label htmlFor="name">First Name :</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Enter your first name"
              value={name}
              onChange={handleInputChange}
            />
          </div>
          <div className="col-25">
            <label htmlFor="name">Last Name :</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Enter your last name"
              value={name}
              onChange={handleInputChange}
            />
          </div>
          <div className="col-25">
            <label htmlFor="email">Email :</label>
          </div>
          <div className="col-75">
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={email}
              onChange={handleInputChange}
            />
          </div>
          <div className="col-25">
            <label htmlFor="date">DOB :</label>
          </div>
          <div className="col-75">
            <input
              type="date"
              id="date"
              name="date"
              placeholder="dd/mm/yyyy"
              value={date}
              onChange={handleInputChange}
            />
          </div>
          <div className="col-25">
            <label htmlFor="name">Education :</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Enter your education"
              value={name}
              onChange={handleInputChange}
            />
          </div>
          <div className="col-25">
            <label htmlFor="name">Location :</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Enter your location"
              value={name}
              onChange={handleInputChange}
            />
          </div>

          {/* <input type="submit" value="Save" /> */}
        </div>
      </form>
    </div>
  );
};

export default Adduser;
